mapper.select:
SELECT yc.id AS commentId, yc.youtube_comment_id AS youtubeCommentId, yc.comment_text AS commentText, yc.commenter_name AS commenterName, yc.published_at AS publishedAt, yc.like_count AS likeCount, yv.id AS videoId, yv.youtube_video_id AS youtubeVideoId, yv.title AS videoTitle, ych.id AS channelId, ych.youtube_channel_id AS youtubeChannelId, ych.channel_name AS channelName, acar.status AS status, acar.reason AS reason, acar.analyzed_at AS analyzedAt, acar.harmfulness_level AS harmfulnessLevel, acar.detection_source AS detectionSource FROM ai_comment_analysis_result acar INNER JOIN youtube_comments yc ON acar.youtube_comment_id = yc.id INNER JOIN youtube_videos yv ON yc.video_id = yv.id INNER JOIN youtube_channels ych ON yv.channel_id = ych.id WHERE yv.id = #{videoId} AND ych.user_id = #{userId} AND ych.deleted_at IS NULL
AND acar.status = #{status}
ORDER BY yc.published_at DESC LIMIT 200

SELECT yc.id AS commentId, yc.youtube_comment_id AS youtubeCommentId, yc.comment_text AS commentText, yc.commenter_name AS commenterName, yc.published_at AS publishedAt, yc.like_count AS likeCount, yv.id AS videoId, yv.youtube_video_id AS youtubeVideoId, yv.title AS videoTitle, ych.id AS channelId, ych.youtube_channel_id AS youtubeChannelId, ych.channel_name AS channelName, acar.status AS status, acar.reason AS reason, acar.analyzed_at AS analyzedAt, acar.harmfulness_level AS harmfulnessLevel, acar.detection_source AS detectionSource FROM ai_comment_analysis_result acar INNER JOIN youtube_comments yc ON acar.youtube_comment_id = yc.id INNER JOIN youtube_videos yv ON yc.video_id = yv.id INNER JOIN youtube_channels ych ON yv.channel_id = ych.id WHERE ych.id = #{channelId} AND ych.user_id = #{userId} AND ych.deleted_at IS NULL
AND acar.status = #{status}
ORDER BY yv.published_at DESC, yc.published_at DESC LIMIT 500

SELECT yc.id AS commentId, yc.youtube_comment_id AS youtubeCommentId, yc.comment_text AS commentText, yc.commenter_name AS commenterName, yc.published_at AS publishedAt, yc.like_count AS likeCount, yv.id AS videoId, yv.youtube_video_id AS youtubeVideoId, yv.title AS videoTitle, ych.id AS channelId, ych.youtube_channel_id AS youtubeChannelId, ych.channel_name AS channelName, acar.status AS status, acar.reason AS reason, acar.analyzed_at AS analyzedAt, acar.harmfulness_level AS harmfulnessLevel, acar.detection_source AS detectionSource FROM ai_comment_analysis_result acar INNER JOIN youtube_comments yc ON acar.youtube_comment_id = yc.id INNER JOIN youtube_videos yv ON yc.video_id = yv.id INNER JOIN youtube_channels ych ON yv.channel_id = ych.id WHERE ych.user_id = #{userId} AND ych.deleted_at IS NULL
AND acar.status = #{status}
ORDER BY ych.id, yv.published_at DESC, yc.published_at DESC LIMIT 1000
