mapper.insert:
INSERT INTO ai_channel_threat_analysis (
    channel_id, channel_name, generated_at, total_comments,
    critical_count, high_count, section_1_threat_intelligence, section_2_defense_strategy
) VALUES (
    #{channelId}, #{channelName}, #{generatedAt}, #{totalComments},
    #{criticalCount}, #{highCount}, 
    #{section1ThreatIntelligence, typeHandler=com.medi.backend.global.config.JsonTypeHandler},
    #{section2DefenseStrategy, typeHandler=com.medi.backend.global.config.JsonTypeHandler}
)

mapper.select:
SELECT * FROM ai_channel_threat_analysis
WHERE channel_id = #{channelId}
  AND generated_at = #{generatedAt}
LIMIT 1

SELECT * FROM ai_channel_threat_analysis
WHERE channel_id = #{channelId}
ORDER BY generated_at DESC, created_at DESC

    LIMIT #{limit}

SELECT COUNT(*) > 0 FROM ai_channel_threat_analysis
WHERE channel_id = #{channelId}
  AND generated_at = #{generatedAt}
